const groupingData = (data = [], filed) => {
    let map = {};
    let dest = [];
    data.forEach(item => {
        if(!map[item[filed]]) {
            dest.push({
                [filed]: item[filed],
                list: [item]
            });
            map[item[filed]] = item;
        } else {
            dest.forEach(dItem => {
              if (dItem[filed] == item[filed]) {
                dItem.list.push(item);
              }
            });
        }
    })
    return dest;
}
